function toggleClass(e,s){e.classList.contains(s)?e.classList.remove(s):e.classList.toggle(s)}function closeNav(){navList.classList.remove("active"),console.log("poop")}var burger=document.querySelector(".burger"),navList=document.querySelector(".list-wrapper"),navItems=document.querySelector(".list-wapper, ul");burger.addEventListener("click",function(){toggleClass(navList,"active"),toggleClass(burger,"open")}),navItems.addEventListener("click",closeNav);