function toggleNav(){navList.classList.contains("active")?navList.classList.remove("active"):navList.classList.toggle("active")}function closeNav(){navList.classList.remove("active"),console.log("poop")}var burger=document.querySelector(".burger"),navList=document.querySelector(".list-wrapper"),navItems=document.querySelector(".list-wapper, ul");burger.addEventListener("click",toggleNav),navItems.addEventListener("click",closeNav);